		[DllImport("libgtk-3-0.dll", CallingConvention = CallingConvention.Cdecl)]
		static extern void gtk_window_unstick(IntPtr raw);

		public void Unstick() {
			gtk_window_unstick(Handle);
		}

#endregion
	}
